document.addEventListener('DOMContentLoaded',()=>{const $navbarBurgers=Array.prototype.slice.call(document.querySelectorAll('.navbar-burger'),0);if($navbarBurgers.length>0){$navbarBurgers.forEach(el=>{el.addEventListener('click',()=>{const target=el.dataset.target;const $target=document.getElementById(target);el.classList.toggle('is-active');$target.classList.toggle('is-active')})})}});$("#exziotech_modal_playbtn").modalVideo();$(document).ready(function(){$(window).scroll(function(){if($(this).scrollTop()>200){$('.ext_backtotop').fadeIn(600)}else{$('.ext_backtotop').fadeOut(600)}});$('.ext_backtotop').click(function(event){event.preventDefault();$('html, body').animate({scrollTop:0},600)})});$(document).on('click','a[href^="#"]',function(event){event.preventDefault();$('html, body').animate({scrollTop:$($.attr(this,'href')).offset().top},1000)});$(window).on('load',function(){$('#status').delay(1500).fadeOut();$('#preloader').delay(1500).fadeOut('slow');$('body').delay(1500).css({'overflow':'visible'})})
$(function(){$('.lazy').lazy()})